mapper.insert:
insert into erp_send_message (send_message_sender, send_message_receiver, send_message_content, send_message_senddate,
send_message_state)
values(#{send_message_sender}, #{send_message_receiver}, #{send_message_content}, #{send_message_senddate}, #{send_message_state});

mapper.update:
update erp_send_message
set send_message_state=1
where send_message_id=#{send_message_id};
