mapper.select:
SELECT m.*, d.dept_name
FROM erp_member_info m
INNER JOIN erp_dept d ON m.dept_key = d.dept_key

	WHERE member_info_key like #{word}


	WHERE member_info_id like #{word}


	WHERE member_info_name like #{word}


	WHERE dept_key like #{word}

ORDER BY member_info_hiredate desc
limit #{start}, #{size}

SELECT count(*) total 
FROM erp_member_info 

	WHERE member_info_key like #{word}


	WHERE member_info_id like #{word}


	WHERE member_info_name like #{word}


	WHERE dept_key like #{word}
